import mongo from 'mongodb'

let connection_string = 'mongodb+srv://admin:admin@fo-cluster.b1r2g.mongodb.net/moja_praksa?retryWrites=true&w=majority'


//slui za pristup prema bazi na navedenom frameworku
let client = new mongo.MongoClient(connection_string,{
    useNewUrlParser: true,
    useUnifiedTopology: true
})

let db = null;

//nakon spajanja vraamo dobivenu instancu baze ostalim modulima koji to trebaju
//exportamo asinkronu funkciju koja ne prima ni jedan parametar

// eksportamo Promise koji resolva na konekciju
export default () => {
    return new Promise((resolve, reject) => {
        // ako smo inicijalizirali bazu i klijent je jo uvijek spojen
        if (db && client.isConnected()) {
            resolve(db);
        } else {
            client.connect((err) => {
                if (err) {
                    reject('Spajanje na bazu nije uspjelo:' + err);
                } else {
                    console.log('Database connected successfully!');
                    db = client.db('moja_praksa');
                    resolve(db);
                }
            });
        }
    });
};

